    public SpriteEditor(SpriteImage image)
    {
        initComponents();

        this.image= image;
        rowCount= image.getHeight();
        colCount= image.getWidth();

        row= -1;
        col= -1;
        pencil= true;

        for(int row= 0; row < rowCount; row++)
        {
            for(int col= 0; col < colCount; col++)
            {
                spriteTable.setValueAt((int) image.getIndexGuide()[row][col],row,col);
            }
        }

        spriteTable.setDefaultRenderer(Object.class, new PaintTableCellRenderer());

        spriteTable.getTableHeader().setUI(null);
        spriteTable.repaint();
        spriteTable.setShowHorizontalLines(true);
        spriteTable.setShowVerticalLines(true);
        spriteTable.setDefaultEditor(Object.class, null);
//        spriteTable.setPreferredSize(new Dimension(400,400));

        setPreferredSize(new Dimension(1200,1200));
        setMinimumSize(new Dimension(500,500));
        pack();
        setVisible(true);
        toFront();


        for(int col= 0; col < image.getPalette().length; col++)
        {
            paletteTable.setValueAt(col,0,col);
        }
        paletteTable.setDefaultRenderer(Object.class, new PaintTableCellRenderer());
        paletteTable.setShowHorizontalLines(true);
        paletteTable.setShowVerticalLines(true);
        paletteTable.setDefaultEditor(Object.class, null);

        paletteTable.getColumnModel().getSelectionModel().addListSelectionListener(e ->
                selectedColor= paletteTable.getSelectedColumn());


        ListSelectionModel spriteTableSelectionModel = spriteTable.getSelectionModel();
        spriteTableSelectionModel.addListSelectionListener(e ->
        {
            if(pencil)
            {
                drawPixel();
            }
            else
            {
                fillWithBucket();
            }
        });

        spriteTable.getColumnModel().getSelectionModel().addListSelectionListener(e ->
        {
            if(pencil)
            {
                drawPixel();
            }
            else
            {
                fillWithBucket();
            }
        });
    }

    private void spriteTableComponentResized(ComponentEvent e)
    {
        //Get new JTable component size
        Dimension size = getSize();

        int cellSize = Math.min(size.height / rowCount, size.width / colCount);

        //Check if height or width is the limiting factor and set cell size accordingly

        //Set new row height to our new size
        spriteTable.setRowHeight(cellSize);

        //Set new column width to our new size
        for (int i = 0; i < spriteTable.getColumnCount(); i++){
            spriteTable.getColumnModel().getColumn(i).setMaxWidth(cellSize);
        }
    }

    private void drawPixel()
    {
        if(row != -1 && col != -1 && (spriteTable.getSelectedRow() != -1 || spriteTable.getSelectedColumn() != -1))
        {
            row= spriteTable.getSelectedRow();
            col= spriteTable.getSelectedColumn();

            spriteTable.setValueAt(selectedColor,row,col);
        }
        else
        {
            row= spriteTable.getSelectedRow();
            col= spriteTable.getSelectedColumn();
        }
    }

    private void fillWithBucket()
    {
        if(row != -1 && col != -1 && (spriteTable.getSelectedRow() != -1 || spriteTable.getSelectedColumn() != -1))
        {
            row= spriteTable.getSelectedRow();
            col= spriteTable.getSelectedColumn();

            fillSurroundings(row,col, (Integer) spriteTable.getValueAt(row,col));
        }
        else
        {
            row= spriteTable.getSelectedRow();
            col= spriteTable.getSelectedColumn();
        }
    }

    private void fillSurroundings(int row, int col, Integer toReplace)
    {
        if(row < 0 || col < 0 || row >= spriteTable.getHeight() || col >= spriteTable.getWidth())
            return;

        if(spriteTable.getValueAt(row,col).equals(toReplace))
        {
            System.out.println("Filling: (" + row + "," + col + ") with " + paletteTable.getValueAt(paletteTable.getSelectedRow(),paletteTable.getSelectedColumn()));
            spriteTable.setValueAt(paletteTable.getValueAt(paletteTable.getSelectedRow(),paletteTable.getSelectedColumn()),row,col);

            fillCell(row+1,col,toReplace);
            fillCell(row-1,col,toReplace);

            fillCell(row,col+1,toReplace);
            fillCell(row,col-1,toReplace);
        }
//        else if(spriteTable.getValueAt(row,col).equals(paletteTable.getValueAt(paletteTable.getSelectedRow(),paletteTable.getSelectedColumn())))
//        {
//            return;
//        }
    }

    private void fillCell(int row, int col, int toReplace)
    {
        if(row < 0 || col < 0 || row >= spriteTable.getHeight() || col >= spriteTable.getWidth())
            return;

        if(spriteTable.getValueAt(row,col).equals(toReplace))
        {
            System.out.println("Filling: (" + row + "," + col + ") with " + paletteTable.getValueAt(paletteTable.getSelectedRow(),paletteTable.getSelectedColumn()));
            fillSurroundings(row,col,toReplace);
        }
    }

    private void spriteTableMouseReleased(MouseEvent e)
    {
        row= -1;
        col= -1;
    }

    private void pencilRadioButtonActionPerformed(ActionEvent e)
    {
        if(pencilRadioButton.isSelected())
        {
            pencil= true;
            bucketRadioButton.setSelected(false);
        }
        else
        {
            pencil= false;
            bucketRadioButton.setSelected(true);
        }
    }

    private void bucketRadioButtonActionPerformed(ActionEvent e)
    {
        if(bucketRadioButton.isSelected())
        {
            pencil= false;
            pencilRadioButton.setSelected(false);
        }
        else
        {
            pencil= true;
            pencilRadioButton.setSelected(true);
        }
    }

