    public class PaintTableCellRenderer extends DefaultTableCellRenderer {

        @Override
        public Component getTableCellRendererComponent(JTable table, Object value, boolean isSelected, boolean hasFocus, int row, int column) {
            super.getTableCellRendererComponent(table, "", isSelected, hasFocus, row, column);
            if (value instanceof Byte)
            {
                byte colorIndex = (byte) value;

                Color color;
                if(colorIndex < 16)
                    color= image.getPalette()[colorIndex];
                else
                    color= image.getPalette()[0];
                setBackground(color);
            }
            else if(value instanceof Integer)
            {
                int colorIndex = (int) value;

                Color color;
                if(colorIndex < 16)
                    color= image.getPalette()[colorIndex];
                else
                    color= image.getPalette()[0];
                setBackground(color);
            }
            else if(value instanceof String)
            {
                byte colorIndex = Byte.parseByte((String) value);

                Color color;
                if(colorIndex < 16)
                    color= image.getPalette()[colorIndex];
                else
                    color= image.getPalette()[0];
                setBackground(color);
            }
            else
            {
                setBackground(image.getPalette()[0]);
            }

            return this;
        }

    }

